class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        @cache
        def d(i, j):
            if i == 0:
                return 1

            k1 = nums[i - 1] - nums[i] + j
            k2 = j

            return s(i - 1, min(k1, k2))

        @cache
        def s(i, j):
            if j < 0:
                return 0
            return (s(i, j - 1) + d(i, j)) % MOD  

        # for i in range(len(nums)):
        #     for j in range(22):
        #         print(i, j, d(i, j))


        return s(len(nums) - 1, nums[-1])
