#define ll long long
const ll mod = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<ll> dp(51);
        ll res = 0;
        for (int i = 0; i <= nums[0]; i++) {
            dp[i] = 1;
        }
        for (int i = 1; i < nums.size(); i++) {
            vector<ll> ndp(51);
            for (int j = 0; j <= 50; j++) for (int k = j; k <= min(50, nums[i]); k++) {
                int prv = nums[i - 1] - j, nxt = nums[i] - k;
                if (prv >= 0 && nxt >= 0 && prv >= nxt) {
                    ndp[k] = (ndp[k] + dp[j]) % mod;
                }
            }
            dp = ndp;
        }
        for (auto x: dp) res = (res + x) % mod;
        return res;
    }
};
