class Solution {
public:
     int countOfPairs(vector<int>& nums) {
        const int mod = (int)1e9+7;
        int n = nums.size();
        vector<int>a(n+1);
        for(int i = 1; i<=n; i++){
            a[i] = nums[i-1];
        }
        vector<vector<int>>dp(n+1,vector<int>(1005));
        vector<vector<int>>psa(n+1,vector<int>(1005));
        if(n==1){
            return a[1]+1;
        }
        for(int j = 0; j<=a[1]; j++){
            dp[1][j] = 1;
            psa[1][j] = j+1;
        }
        for(int i = 2; i<=n; i++){
            int dif = a[i] - a[i-1];

            for(int j = 0; j<=a[i]; j++){
                if(j-dif>=0)
                    dp[i][j] += psa[i-1][min(j-dif,j)];
                dp[i][j]%=mod;
            }
            int s = 0;
            for(int j = 0; j<1005; j++){
                s+=dp[i][j];
                s%=mod;
                psa[i][j] = s;
            }
        }
        return psa[n][1004];
    }
};
