class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [[0] * 51 for _ in range(51)]

        for x in range(nums[0] + 1):
            dp[x][nums[0] - x] = 1

        for i in range(1, len(nums)):
            new_dp = [[0] * 51 for _ in range(51)]
            for x in range(nums[i] + 1):
                for y in range(x + 1):
                    if nums[i] - x <= nums[i - 1] - y:
                        new_dp[x][nums[i] - x] += dp[y][nums[i - 1] - y]

            dp = new_dp

        ans = 0
        for x in range(51):
            for y in range(51):
                ans += dp[x][y]

        return ans % (10 ** 9 + 7)
