class Solution {
    final long mod = 1_000_000_007L;
    public int countOfPairs(int[] nums) {
        long ans = 0;
        long[][] dp = new long[nums.length][51];
        for(long[] d:dp) Arrays.fill(d,-1);
        for(int i=0;i<=nums[0];i++){
            ans+=rec(nums,1,i,dp);
            ans%=mod;
        }
        return (int)ans;
    }
    public long rec(int[] nums,int i,int pre, long[][] dp){
        if(i==nums.length) return 1;
        if(dp[i][pre]!=-1) return dp[i][pre];
        long ans = 0;
        for(int j=pre;j<=nums[i];j++){
            if(nums[i-1]-pre>=nums[i]-j) {
                ans+=rec(nums,i+1,j,dp);
                ans%=mod;
            }
        }
        return dp[i][pre] = ans;
    }
}
