class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        n = len(nums)
        dp = [[0] * (nums[i] + 1) for i in range(n)]
        for i in range(len(dp[0])):
            dp[0][i] = 1


        for j in range(1, n):
            prefix = dp[j-1]
            for k in range(1, len(prefix)):
                prefix[k] += prefix[k-1]
            print(prefix)

            for l in range(nums[j]+1):
                ind = min(l, nums[j-1] - nums[j] + l)
                if ind < 0:
                    dp[j][l] = 0
                else:
                    dp[j][l] = prefix[ind] % mod

        return sum(dp[-1]) % mod
