class Solution {
public:
    int countOfPairs (vector<int> &A) {
        long long MOD = 1e9 + 7; int N = size(A);

        array<array<long long, 51>, 2001> DP;
        for (auto &i : DP) for (auto &j : i) j = 0;

        for (int i = 1; i <= N; i++) {
            for (int j = 0; j <= A[i - 1]; j++) {
                if (i == 1) { DP[i][j] = 1; continue; }
                for (int k = j; k <= A[i - 2]; k++) {
                    if (A[i - 2] - k > A[i - 1] - j) continue;
                    DP[i][j] += DP[i - 1][k]; DP[i][j] %= MOD;
                }
            }
        }

        long long R = 0LL;
        for (auto &i : DP[N]) R += i, R %= MOD;

        return R;
    }
};
