class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9+7
        @cache
        def dfs(i, l1, l2):
            if i==len(nums):
                return 1
            if i==0:
                res=0
                for x in range(nums[i]+1):
                    one = x
                    two = nums[i]-x
                    res+=dfs(1,one,two)
                    res%=MOD
                return res
            else:
                res = 0
                for x in range(l1,nums[i]+1):
                    one = x
                    two = nums[i]-x
                    if two<=l2:
                        res+=dfs(i+1,one,two)
                        res%=MOD

                return res
        a = dfs(0,0,0)
        dfs.cache_clear()
        return a
