class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        if len(nums) == 1:
            return nums[0] + 1

        n = len(nums)
        dp = [[0 for i in range(50 + 5)] for j in range(n)]
        for i in range(51):
            dp[0][i] = 1

        for i in range(1, n):
            diff = nums[i] - nums[i - 1]
            if diff < 0:
                diff = 0
            for j in range(nums[i] + 1):
                for k in range(j + diff, nums[i] + 1):
                    dp[i][k] += dp[i - 1][j]
                    dp[i][k] %= MOD
        # print(dp)
        return sum(dp[-1]) % MOD
