#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<long long>> dp(n, vector<long long>(51, 0));

        for (int i = 0; i <= nums[0]; i++) {
            dp[0][i] = 1;
        }

        for (int i = 1; i < n; i++) {
            for(int j=0;j<=nums[i];j++){
                for(int s=0;s<=j;s++){
                    if(nums[i-1]-s>=nums[i]-j){
                        dp[i][j]+=dp[i-1][s]%MOD;
                        dp[i][j]%=MOD;
                    }
                }
            }
        }

        long long result = 0;
        for (int i = 0; i <= nums[n-1]; i++) {
            result = (result + dp[n - 1][i]) % MOD;
        }

        return result%MOD;
    }
};
