class Solution:
    def countOfPairs(self, a: List[int]) -> int:
        n = len(a)
        f = [[0] * (51) for _ in range(n+1)]
        mod = 10**9 + 7
        f[0][0] = 1
        for i, x in enumerate(a):
            for j in range(x+1):
                bi = j
                ci = x - j
                for k in range(j+1):
                    cii = a[i - 1] - k if i > 0 else inf
                    if ci <= cii:
                        f[i+1][j] += f[i][k]
                        f[i+1][j] %= mod

        return sum(f[n][:]) % mod
