class Solution {
public:
    int mod=1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(), p=0;
        // vector<deque<int>>vdq(n);
        vector<vector<int>>dp(n, vector<int>(51)), right(n, vector<int>(51));
        for(int i=0;i<=nums[0];i++){
            dp[0][i]=1;
        }

        for(int i=1;i<n;i++){
            for(int j=0;j<=nums[i-1];j++){
                for(int k=j;k<=nums[i];k++){
                    if(nums[i]-k<=nums[i-1]-j){
                        dp[i][k]+=dp[i-1][j];
                        dp[i][k]%=mod;
                    }

                }
            }
        }

        int ans=0;
        for(int i=0;i<=50;i++){
            ans+=dp[n-1][i];
            ans%=mod;
        }
        return ans;

    }
};
