typedef long long int ll;
#define M 1000000007
#define MX 51

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n = nums.size(); ll dp[n][MX][MX];
        for(int i = 0; i < n; i++){
            for(int j = 0; j < MX; j++){
                for(int k = 0; k < MX; k++){
                    dp[i][j][k] = 0;
                }
            }
        }


        for(int i = 0; i < n; i++){
            for(int j = 0; j < MX; j++){
                ll k = nums[i] - j;

                if(i == 0){
                    if(k >= 0) dp[i][j][k]++;
                }
                else{
                    if(k >= 0){
                        for(int jp = 0; jp <= j; jp++){
                            if(i == 0){

                            }
                            else{
                                ll kp = nums[i - 1] - jp;
                                if(kp >= 0 && kp >= k){
                                    dp[i][j][k] += dp[i-1][jp][kp];
                                    dp[i][j][k] %= M;
                                }
                            }

                        }
                    }
                }

            }
        }

        ll ans = 0;

        for(int i = 0; i < MX; i++){
            for(int j = 0; j < MX; j++){
                ans += dp[n - 1][i][j]; ans %= M;
            }
        }

        return ans;

    }
};
