class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        high = int(1e4)
        n = len(nums)
        @cache
        def dp(i: int, p: int):
            if i == n:
                return 1
            p2 = high if i == 0 else nums[i - 1] - p
            ans = 0
            for k in range(nums[i] + 1):
                k2 = nums[i] - k
                if p2 >= k2 and p <= k:
                    ans += dp(i + 1, k)
                    ans %= int(1e9 + 7)
            return ans 
        return dp(0, 0) % int(1e9 + 7)
