class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        dp = [[0] * 51 for _ in range(n)]

        for j in range(nums[0] + 1):
            dp[0][j] = 1

        for i in range(1, n):
            for j in range(nums[i] + 1):
                arr2_i = nums[i] - j
                for k in range(j + 1):
                    arr2_i_1 = nums[i-1] - k
                    if arr2_i <= arr2_i_1:
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD

        result = sum(dp[n-1][j] for j in range(51)) % MOD

        return result
