class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [[1] * (num + 1) for num in nums]

        for i in range(len(nums) - 2, -1, -1):
            for j in range(nums[i] + 1):
                dp[i][j] = sum(dp[i + 1][max(j, nums[i + 1] - (nums[i] - j)):])
        return sum(dp[0]) % 1000000007
