class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long i, j, k, n = nums.size(), ans = 0, dp[n][51], x, y, cnt1, cnt2, mod = 1e9 + 7;
        for(i=0;i<n;i++)    for(j=0;j<51;j++)   dp[i][j] = 0;
        for(j=0;j<=nums[0];j++) dp[0][j] = 1;

        for(i=1;i<n;i++){
            for(j=0;j<=nums[i];j++){
                x = j; y = nums[i] - x;
                cnt1 = 0; cnt2 = 0;
                for(k=0;k<=x;k++)           
                    if(nums[i-1] - k >= y)  
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % mod;
            }
        }

        for(j=0;j<=nums[n-1];j++) ans = (ans + dp[n-1][j]) % mod;

        return ans;
    }
};
