class Solution {
    public long MOD = 1000000007;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[][] dp = new long[n][51];
        for(int i = n-1; i >= 0; i--) {
            for(int j = 0; j <= nums[i]; j++) {
                if(i==n-1) dp[i][j] = 1;
                else {
                    for(int k = j; k <= nums[i+1]; k++) {
                        if(nums[i]-j >= nums[i+1]-k) dp[i][j] = (dp[i][j] + dp[i+1][k])%MOD;
                    }
                }
            }
        }
        long ans = 0;
        for(int i = 0; i <= nums[0]; i++) ans = (ans + dp[0][i])%MOD;
        return (int) ans;
    }
}
