class Solution {
public:
    using LL = long long;
    LL M = 1e9+7;
    LL dp[2001][51];
    int countOfPairs(vector<int>& nums) {
        memset(dp, 0, sizeof(dp));
        int n = nums.size();
        for (int j=0; j<=nums[0]; j++) {
            dp[0][j] = 1;
        }
        for (int i=1; i<n; i++) {
            for (int j=0; j<=nums[i]; j++) {
                for (int k=0; k<=j; k++) {
                    if (nums[i-1]-k >= nums[i]-j) {
                        dp[i][j] += dp[i-1][k];
                        dp[i][j] %= M;
                    }
                }
            }
        }
        LL ans = 0;
        for (int j=0; j<=50; j++) {
            ans += dp[n-1][j];
            ans %= M;
        }
        return ans;
    }
};
