class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int mod = 1e9+7;
        using ll = long long;
        ll n = nums.size();
        vector<vector<ll>> dp(n,vector<ll>(55,0)); // arr1 val
        for(ll i = 0;i<=nums[0];i++){
            dp[0][i] = 1;
        }
        for(ll i = 1;i<n;i++){
            for(ll j = 0;j<=nums[i];j++){
                ll mi = 0, mx = min(j,nums[i-1]-nums[i]+j); // k<=j num[i-1]-num[i]+j>=k;
                for(ll k = mi;k<=mx;k++){
                    dp[i][j] = (dp[i][j]+dp[i-1][k])%mod;
                }
            }
        }
        ll ans = 0;
        for(ll j = 0;j<=nums[n-1];j++) ans += dp[n-1][j];
        return ans%mod;
    }
};
