const M: i32 = 1000000007;

impl Solution {
    pub fn count_of_pairs(nums: Vec<i32>) -> i32 {
        let n = nums.len();
        let mut dp = vec![vec![0; 51]; n];
        for j in 0..=nums[0] as usize {
            dp[0][j] = 1;
        }
        for i in 1..n {
            for j in 0..=nums[i] as usize {
                for k in 0..=j as usize {
                    if nums[i - 1] - k as i32 >= nums[i] - j as i32 {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % M;
                    }
                }
            }
        }

        let mut result = 0;
        for j in 0..=50 {
            result = (result + dp[n - 1][j]) % M;
        }
        result
    }
}
