class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        n = len(nums)
        dp = [[0] * (nums[i] + 1) for i in range(n)]
        for i in range(nums[0] + 1):
            dp[0][i] = 1

        for i in range(1, n):
            curr = 0
            if nums[i] > nums[i-1]:
                for n1 in range(nums[i] - nums[i-1], nums[i] - nums[i-1] + nums[i-1] + 1):
                    dp[i][n1] = dp[i - 1][n1 - (nums[i] - nums[i-1])] + dp[i][n1 - 1]
                    dp[i][n1] %= mod
            else:
                for n1 in range(nums[i] + 1):
                    dp[i][n1] = dp[i - 1][n1] + dp[i][n1 - 1]
                    dp[i][n1] %= mod
        res = 0
        for i in dp[-1]:
            res += i
            res %= mod
        return res
