class Solution {
public:
static const int MODULO = 1e9 + 7;
    vector<vector<int>> dp;
    vector<int> num;

    int rec(int ind, int sum) {
        if (ind == num.size()) {
            return 1; 
        }

        if (dp[ind][sum] != -1) {
            return dp[ind][sum]; 
        }

        int ans = 0;
        int k = (ind == 0) ? 50 : num[ind - 1] - sum;

        for (int current_sum = sum; current_sum <= num[ind]; ++current_sum) {
            int left = num[ind] - current_sum;
            if (left <= k) {
                ans = (ans + rec(ind + 1, current_sum)) % MODULO;
            }
        }

        dp[ind][sum] = ans;
        return ans;
    }
    int countOfPairs(vector<int>& nums) {
        num = nums;
        int n = nums.size();
        dp.assign(n, vector<int>(2001, -1));

        return rec(0, 0);
    }
};
