class Solution {
public:
    int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(1001, 0));
        vector<vector<int>> dpCum(n, vector<int>(1001, 0));
        int start = 0;
        int end = nums[0];
        for(int i = 0; i < n; i++) {
            int diff = 0;
            if(i > 0) {
                diff = max(0, nums[i] - nums[i - 1]);
            }
            for(int j = diff; j <= nums[i]; j++) {
                if(i == 0) {
                    dp[i][j] = 1;
                }
                else {
                    dp[i][j] = dpCum[i - 1][j - diff];
                }
                if(j == 0) dpCum[i][j] = dp[i][j];
                else {
                    long long temp = dpCum[i][j - 1] + dp[i][j];
                    temp %= MOD;
                    dpCum[i][j] = temp;
                }
            }
        }
        return dpCum[n - 1][nums[n - 1]];
    }
};
