class Solution {
public:
    int dp[2005][1005];
    const int mod=1e9+7;
    int countOfPairs(vector<int>& nums) {

        for(int i = 0;i<nums.size();i++){
            for(int j=0;j<=1000;j++)dp[i][j]=0;
        }
        for(int i = 0;i<=nums[0];i++)dp[0][i]=i+1;
        for(int i = 1;i<nums.size();i++){
            for(int j = 0;j<=nums[i];j++){
                int x=nums[i]-j;
                int r=min(j,nums[i-1]-x);
                if(r>=0)
                dp[i][j]=dp[i-1][r];
                else dp[i][j]=0;    
            }
            for(int j = 1;j<=nums[i];j++)dp[i][j]+=dp[i][j-1],dp[i][j]%=mod;
        }
        return dp[nums.size()-1][nums.back()];
    }
};
