class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ios_base::sync_with_stdio(0) ;
        cin.tie(0) ;
        cout.tie(0) ;
        int n = nums.size() ;
        int m = 0 ;
        const long long MOD = 1e9 + 7 ;
        for(int i=0;i<n;i++) m = max(nums[i],m) ;
        vector<long long> dp(m+1,0) ;
        for(int i=0;i<=nums[0];i++) dp[i] = 1 ;
        for(int i=1;i<n;i++) {
            vector<long long> temp(m+1,0) ;
            for(int c=0;c<=nums[i];c++) {
                for(int a=0;a<=min(nums[i-1],c);a++) {
                    int b = nums[i-1] - a ;
                    int d = nums[i] - c ; 
                    if(b>=d) { 
                        temp[c] = (temp[c]+dp[a])%MOD ;
                    }
                }
            } 
            dp = temp ;
        }
        long long ans = 0 ;
        for(int i=0;i<=m;i++) {
            ans = (ans+dp[i])%MOD ;
        }
        return ans ;
    }
};
