#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int mod = 1e9 + 7;
        vector<vector<long long>> dp(n, vector<long long>(51, 0));
        vector<long long> prefix(52, 0);

        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
            prefix[j + 1] = (prefix[j] + dp[0][j]) % mod;
        }

        for (int i = 1; i < n; i++) {
            vector<long long> prefix2(52, 0);
            for (int j = 0; j <= nums[i]; j++) {
                int prevmax = min(j, nums[i-1] - (nums[i] - j));
                if (prevmax >= 0) {
                    dp[i][j] = prefix[prevmax + 1];
                } else {
                    dp[i][j] = 0;
                }
                prefix2[j + 1] = (prefix2[j] + dp[i][j]) % mod;
            }
            prefix = prefix2;
        }

        long long ans = 0;
        for (int j = 0; j <= 50; j++) {
            ans = (ans + dp[n-1][j]) % mod;
        }

        return ans;
    }
};
