class Solution {
public:
    const int mod = 1e9+7;
    long long dp[2001][52][52];
    long long solve(int idx,int preva, int prevb, vector<int>&nums){
        if(idx>=nums.size()){
            return 1; 
        } 
        if(dp[idx][preva][prevb]!=-1){
            return dp[idx][preva][prevb]; 
        } 

        int x=preva,y=prevb; 

        if(x==51)x=-1; 
        if(y==51)y=51;

        long long ans = 0;

        for(int i=0;i<=nums[idx];++i){
            int a = i; 
            int b = nums[idx]-i;

            if(x<=a && y>=b){
                ans+=solve(idx+1,a,b,nums); 
                ans%=mod;
            }
        } 
        return dp[idx][preva][prevb]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        for(int i=0;i<=nums.size();++i){
            for(int j=0;j<52;++j){
                for(int k=0;k<52;++k){
                    dp[i][j][k]=-1;
                }
            }
        }
        return solve(0,51,51,nums);
    }
};
