class Solution {
    vector<vector<int>> dp;
    int mod = 1e9+7;

    int f(int i, int j, vector<int> &nums, int &n){
        if(i == n) return 1;

        if(dp[i][j] != -1) return dp[i][j];

        dp[i][j] = 0;

        for(int val = 0; val <= nums[i]; ++val){
            int a1 = val;
            int a2 = nums[i] - a1;

            if(i == 0 || (i > 0 && a1 >= j && a2 <= (nums[i-1]-j))){
                dp[i][j] += f(i+1, a1,nums, n);
            }
        }

        return dp[i][j];
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.assign(n+1, vector<int> (51, 1));

        for(int i = n-1; i >= 0; --i){
            for(int j = 0; j <= 50; ++j){
                if(i > 0 && j > nums[i-1]) break;

                dp[i][j] = 0;

                for(int val = 0; val <= nums[i]; ++val){
                    int a1 = val;
                    int a2 = nums[i] - a1;

                    if((i == 0) || (i > 0 && a1 >= j && a2 <= (nums[i-1]-j))){
                        dp[i][j] += dp[i+1][a1];
                        dp[i][j] %= mod;
                    }
                }
            }
        }


        return dp[0][0];
    }
};
