class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<int> a(n), b(n);
        for(int i = n - 2; i >= 0; i--) {
            if(nums[i] >= nums[i + 1]) b[i] = b[i + 1] + nums[i] - nums[i + 1];
            else b[i] = b[i + 1];
        }
        if(b[0] > nums[0]) return 0;
        a[0] = nums[0] - b[0];
        vector<long long> dp(a[0] + 1, 1);
        for(int j = 0 ; j < n; j++) {
            for(int i = 1; i <= a[0]; i++) {
                dp[i] += dp[i-1];
                dp[i] %= 1000000007;
            }
        }
        return dp[a[0]];
    }
};
