class Solution {
public:
    typedef long long ll;
    ll mod=1e9+7;
    vector<vector<ll> > dp;
    int countOfPairs(vector<int>& numss) {
        vector<ll> a;
        for(int i=0;i<numss.size();i++){
            a.push_back(numss[i]);
        }
        ll n=a.size();
        dp.resize(n+2);
        for(int i=0;i<dp.size();i++){
            dp[i].resize(55,0);
        }
        for(ll j=0;j<=a[0];j++){
            dp[0][j]=1;
        }
        for(ll i=1;i<a.size();i++){
            for(ll j=0;j<=a[i];j++){
                for(ll k=0;k<=j;k++){
                    if(a[i]-j<=a[i-1]-k){
                        dp[i][j]=(dp[i][j]+dp[i-1][k])%mod;
                    }
                }
            }
        }
        ll ans=0;
        for(ll i=0;i<=a[n-1];i++){
            ans+=dp[n-1][i];
        }
        return ans%mod;
    }
};
