func countOfPairs(nums []int) int {
    maxNum := slices.Max(nums)
    counts := make([][]int, maxNum + 1)
    for n := 0; n <= maxNum; n++ {
        counts[n] = make([]int, len(nums))
        counts[n][0] = 1
    }
    m := 1000000007
    for i := 1; i < len(nums); i++ {
        for x := 0; x <= nums[i]; x++ {
            y := nums[i] - x
            j := min(x, nums[i - 1] - y)
            for j >= 0 {
                counts[x][i] = (counts[x][i] + counts[j][i - 1] % m) % m
                j--
            }
        }
    }
    total := 0
    for x := 0; x <= nums[len(nums) - 1]; x++ {
        total = (total + counts[x][len(nums) - 1] % m) % m
    }
    return total
}
