class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        N = len(nums)
        MOD = 10 ** 9 + 7

        # (end1, end2) => count
        D = {}
        D[(0, 50)] = 1

        for i in range(N):
            D2 = {}
            for a in range(nums[i] + 1):
                b = nums[i] - a
                for (end1, end2), count in D.items():
                    if a >= end1 and 0<= b <= end2:
                        D2[(a, b)] = (D2.get((a, b), 0) + count) % MOD
            D = D2

        return sum(D.values()) % MOD
