#define lli long long
lli mod=1e9+7;
class Solution {
public:
    lli fuc(lli i,lli j,vector<int>&nums,vector<vector<lli>>&dp){
        lli n=nums.size();
        if(i==n){
            return 1;
        }
        if(dp[i][j]!=-1){
            return dp[i][j];
        }
        lli k=1e9;
        if(i!=0){
            k=nums[i-1]-j;
        }
        //cout<<i<<" "<<j<<" "<<k<<endl;
        lli ans=0;
        for(lli t=0;t<=nums[i];t++){
            if(i==0){
                ans+=fuc(i+1,t,nums,dp)%mod;
                ans=ans%mod;
            }
            else{
                if(t>=j&&nums[i]-t<=k){
                    ans+=fuc(i+1,t,nums,dp)%mod;
                    ans=ans%mod;
                }
            }
        }
        return dp[i][j]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<lli>>dp(2002,vector<lli>(51,-1));
        return (int)(fuc(0,0,nums,dp)%mod);
    }
};
