class Solution {
    Long[][][] mem;
    int[] nums;
    int mod = (int)1e9 + 7;
    public int countOfPairs(int[] nums) {
        this.nums = nums;
        mem = new Long[52][52][nums.length];
        return (int)(dfs(0, 50, 0) % mod);
    }

    long dfs(int start1, int start2, int i) {
        if (i >= nums.length)
            return 1;

        if (mem[start1][start2][i] != null)
            return mem[start1][start2][i];
        long ans = 0;
        for (int j = 0; j <= nums[i]; j++)  {
            if (j < start1)
                continue;
            if (nums[i] - j > start2)
                continue;
            ans = (ans + dfs(j, nums[i] - j, i + 1)) % mod;
        }
        mem[start1][start2][i] = ans;
        return ans;
    }
}
