class Solution {
public:
    long long MOD = 1e9 + 7;
    long long f(int i, int n, int last1, int last2, vector<int> &nums, vector<vector<int>> &dp) {
        if(i == n) {
            return 1;
        }
        if(dp[i][last1] != -1) return dp[i][last1];
        long long ans = 0;
        for(int j = last1; j <= nums[i]; j++) {
            if(nums[i] - j <= last2) {
                ans += f(i + 1, n, j, nums[i] - j, nums, dp);
                ans %= MOD;
            }
        }
        return dp[i][last1] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int maxi = *max_element(nums.begin(), nums.end());
        vector<vector<int>> dp(n, vector<int>(maxi + 1, -1));
        return f(0, n, 0, 51, nums, dp);
    }
};
