class Solution {
public:
    const int MOD = 1e9 + 7;

    // Function to initialize the DP table for the first element
    void initializeDP(vector<vector<int>>& dp, vector<int>& nums) {
        for (int arr1_0 = 0; arr1_0 <= nums[0]; ++arr1_0) {
            dp[0][arr1_0] = 1;
        }
    }

    // Function to calculate the prefix sum for efficient range sum computation
    vector<int> calculatePrefixSum(vector<int>& dp_row, int maxi) {
        vector<int> prefix_sum(maxi + 1, 0);
        for (int v = 0; v < maxi; ++v) {
            prefix_sum[v + 1] = (prefix_sum[v] + dp_row[v]) % MOD;
        }
        return prefix_sum;
    }

    // Function to update the DP table for each index
    void updateDP(vector<vector<int>>& dp, vector<int>& nums, int n, int maxi) {
        for (int i = 1; i < n; ++i) {
            vector<int> prefix_sum = calculatePrefixSum(dp[i - 1], maxi);

            for (int arr1_i = 0; arr1_i <= nums[i]; ++arr1_i) {
                int arr2_i = nums[i] - arr1_i;
                if (arr1_i >= maxi || arr2_i >= maxi || arr2_i < 0) continue;

                int low = max(0, nums[i - 1] - maxi + 1);
                int high = min(arr1_i, nums[i - 1] - arr2_i);

                if (low <= high) {
                    dp[i][arr1_i] = (prefix_sum[high + 1] - prefix_sum[low] + MOD) % MOD;
                }
            }
        }
    }

    // Main function to count the number of monotonic pairs
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int maxi = 51;  // As the maximum value in nums is 50
        vector<vector<int>> dp(n, vector<int>(maxi, 0));

        initializeDP(dp, nums);
        updateDP(dp, nums, n, maxi);

        int result = 0;
        for (int v = 0; v < maxi; ++v) {
            result = (result + dp[n - 1][v]) % MOD;
        }

        return result;
    }
};
