class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        mx = max(nums)
        dp = [0] * (mx + 1)
        for i in range(nums[0] + 1):
            dp[i] += 1

        for i in range(1, len(nums)):
            num = nums[i]
            prevNum = nums[i - 1]
            for x1 in range(mx, -1, -1):
                x2 = num - x1
                cnt = 0
                if x2 >= 0:
                    for prev1 in range(x1 + 1):
                        prev2 = prevNum - prev1
                        if prev2 >= 0 and prev2 >= x2:
                            cnt = (cnt + dp[prev1]) % MOD
                dp[x1] = cnt                

        return sum(dp) % MOD
