class Solution {
    int MOD = (int) 1e9 + 7;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[][] f = new long[n][51];
        for (int a = 0; a <= nums[0]; a++) {
            f[0][a] = 1;
        }
        for (int i = 1; i < f.length; i++) {
            int num = nums[i];
            for (int a = 0; a <= num; a++) {
                int b = num - a;
                for (int preA = 0; preA <= a; preA++) {
                    int preNum = nums[i - 1];
                    int preB = preNum - preA;
                    if (preB >= b) f[i][a] = (f[i][a] + f[i - 1][preA]) % MOD;
                }
            }
        }
        long res = 0l;
        for (int i = 0; i <= 50; i++) {
            res = (res + f[f.length - 1][i]) % MOD;
        }
        return (int) res;
    }
}
