class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int const M = 1e9 + 7;
        vector<vector<int>> dp(n, vector<int>(51));
        for(int x = 0; x <= nums[0]; x++)
                dp[0][x] = 1;

        for(int i = 1; i < n; i++) {
            for(int x = 0; x <= nums[i]; x++) {
                // dp[i][x]
                for(int y = x - max(0, nums[i] - nums[i - 1]); y >= 0; y--) {
                    dp[i][x] += dp[i-1][y];
                    dp[i][x] %= M;
                }
            }
        }

        int ans = 0;
        for(int x = 0; x <= 50; x++) {
            ans += dp[n-1][x];
            ans %= M;
        }
        return ans;
    }
};
