from functools import cache
M = 10**9 + 7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        @cache
        def dfs(i, a, b):
            if i == n:
                return 1
            res = 0
            for a2 in range(a, nums[i] + 1):
                b2 = nums[i] - a2
                if b2 <= b:
                    res += dfs(i+1, a2, b2)
                    res %= M
            return res
        return dfs(0, 0, nums[0])
