class Solution {
public:
    const int N=500010;
    const int mod=1e9+7;
    const int atcoderMod=998244353;


    int add(int x,int y){int res=(x+y)%mod;return res<0?res+mod:res;}
    int mul(int x,int y){int res=(x*1LL*y)%mod;return res<0?res+mod:res;}

    vector<int> a;
    int n;
    int dp[2010][1010];

    #define dpp dp[id][afar]

    int solve(int id, int afar) {
        // cout<<id<<' '<<afar<<endl;
        if(dpp!=-1) return dpp;
        int bfar = a[id-1]-afar;
        if(id==n) {
            return min(a[id-1]+1-afar, bfar+1);
        }
        if(afar > a[id]) return dpp=0;


        int mafar = max(afar, a[id]-bfar);
        dpp=0;
        if(mafar <= a[id]) {
            dpp=add(solve(id+1, mafar), -solve(id+1, a[id]+1));
        }
        dpp=add(dpp, solve(id, afar+1));

        // cout<<id<<' '<<afar<<"::"<<dpp<<endl;
        // assert(afar <= a[id]);
        // for(int i=a[id];i>=mafar;i--) {
        //     int nbfar=a[id]-i;
        //     // if(nbfar > bfar) break;
        //     // cout<<"ERROR::"<<i<<' '<<id<<endl;
        //     dpp=add(dpp, solve(id+1, i));
        // }
        return dpp;
    }

    int countOfPairs(vector<int>& nums) {
        for(auto i: nums) a.push_back(i);
        n=nums.size();
        memset(dp, -1, sizeof dp);
        int ans=0;
        ans=add(solve(1,0), -solve(1,a[0]+1));
        // for(int i=0;i<=a[0];i++) {
        //     ans=add(ans, solve(1, i));
        // }
        return ans;
    }
};
