class Solution {
public:
    int mod=1e9+7;
    int countOfPairs(vector<int>& nums) {
        vector<long long> prev(51,0),pres(51,0);
        int size=nums.size();
        for(int i=0;i<=nums[0];i++)prev[i]=1;
        for(int i=1;i<size;i++)
        {
            for(int j=0;j<=nums[i];j++)
            {
                for(int k=0;k<=nums[i-1];k++)
                {
                   if(j>=k && nums[i]-j<=nums[i-1]-k)
                   {
                       pres[j]+=prev[k];
                       pres[j]%=mod;
                   }
                }
            }
            for(int j=0;j<51;j++)
            {
               // cout<<pres[i]<<" ";
               prev[j]=pres[j];
               if(j>nums[i])prev[j]=0;
               pres[j]=0;
            }
            // cout<<endl;
        }
        long long sum=0;
        for(int i=0;i<51;i++)
        {
            sum+=prev[i];
            sum%=mod;
        }
        return (int)sum;
    }
};
