class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        ll dp[n+1][51][51];
        for(ll i=0;i<=n;i++){
        for(ll j=0;j<=50;j++){
            for(ll k=0;k<=50;k++){
            dp[i][j][k] = 0;
            }
        }
        }
        for(ll i=0;i<=nums[0];i++){
            dp[0][i][nums[0]-i] = 1;
        }
        for(ll i=1;i<=n-1;i++){
        for(ll j=0;j<=nums[i-1];j++){
            for(ll k=j;k<=nums[i];k++){
                if(nums[i]-k <= nums[i-1]-j){
                // trace(i,j,k,dp[i-1][j][nums[i-1]-j]);
                    dp[i][k][nums[i]-k] += dp[i-1][j][nums[i-1]-j];
                    dp[i][k][nums[i]-k]%=mod;
                }
            }
        }
        }
        int ans = 0;
        for(ll i=0;i<=nums[n-1];i++){
        // trace(dp[n-1][i][nums[n-1]-i]);
        ans += dp[n-1][i][nums[n-1]-i];
        ans %= mod;
        }
        return ans;
    }
};
