const int N = 2001;
const int mod = (int)1e9 + 7;

class Solution {
public:

/*
    10

    4
*/

    vector<int> a;
    int dp[N][51];
    int calc(int pos, int l1) {
        // cout << l1 << ' ' << l2 << endl;
        // cout << pos << ' ' << l1 << endl;
        if (pos == a.size()) return 1;
        if (~dp[pos][l1]) return dp[pos][l1];
        int res = 0;
        for (int i = l1; i <= a[pos]; i++) {
            int j = a[pos] - i;
            if (pos && j > a[pos - 1] - l1) continue;

            res += calc(pos + 1, i);
            res %= mod;
        }
        return dp[pos][l1] = res;
    }
    int countOfPairs(vector<int>& a_) {
        a = a_;
        memset(dp, -1, sizeof(
            dp));
        return calc(0, 0);
    }
};
