using ll = long long;
constexpr int mod = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& ns) {
        vector<int> dp(64);
        for (int i = 0; i <= ns[0]; ++i) dp[i] = 1;
        for (int i = 1; i < ns.size(); ++i) {
            int v = ns[i], p = ns[i - 1];
            vector<int> ndp(64);

            /*
            int acc = 0;
            for (int j = 0; j <= v; ++j) {
                acc = (acc + dp[j]) % mod;
                ndp[j] = acc;
            }
            */

            for (int j = 0; j <= v; ++j) {
                int acc = 0;
                for (int x = 0; x <= j; ++x) {
                    if (p - x >= v - j) {
                        acc = (acc + dp[x]) % mod;
                    }
                }
                ndp[j] = acc;
            }

            dp.swap(ndp);
        }


        int ans = 0;
        for (auto x: dp) ans = (ans + x) % mod;
        return ans;
    }
};
