class Solution {
    int large = 1e9;
    int MOD=1e9+7;
    int solve(int idx,int ld, vector<vector<int>>&dp, vector<int>&nums){
        if(idx==nums.size())return 1;
        if(dp[idx][ld]!=-1)return dp[idx][ld];
        int lst1=ld,end,count=0;
        if(idx==0)end=large;
        else end = nums[idx-1]-ld;
        for(int i=lst1;i<=nums[idx];i++){
            int j=nums[idx]-i;
            if(j>=0 && j<=end){
                count = (0ll + count + solve(idx+1,i,dp,nums))%MOD;
            }
        }
        return dp[idx][ld]=count;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(n,vector<int>(500,-1));
        return solve(0,0,dp,nums);
    }
};
