class Solution {
public:

    long long Count(std::vector<int>& nums, int idx, int arr_1_min, int arr_2_max, std::vector<std::vector<std::vector<int>>>& cache){

        if(idx >= nums.size()){
            return 1;
        }

        int mod = 1e9 + 7;
        long long result = 0;

        if(cache[idx][arr_1_min][arr_2_max] != -1){
            return cache[idx][arr_1_min][arr_2_max];
        }

        for(int i = max(arr_1_min, nums[idx] - arr_2_max); i <= nums[idx]; i++){

            if(nums[idx] - i > arr_2_max) continue;

            long long current = (Count(nums, idx+1, i, nums[idx] - i, cache))%mod;

            result += current;
            result %= mod;
        }

        return cache[idx][arr_1_min][arr_2_max] = result;
    }

    int countOfPairs(std::vector<int>& nums) {

        std::vector<std::vector<std::vector<int>>> cache(nums.size(), std::vector<std::vector<int>>(51, std::vector<int>(51, -1))); 


        return Count(nums, 0, 0, 50, cache);
    }


};
