MOD = 10**9 + 7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        d = defaultdict(int)

        for i, n in enumerate(nums):
            for v in range(n + 1):
                if i == 0:
                    d[(i, v)] = 1
                else:
                    # implies other element is n - v, implying i - 1 >= n1 - v
                    n1 = nums[i - 1]
                    d[(i, v)] = d[(i, v - 1)] + d[(i - 1, min(v, n1 - (n - v)))]

        #print(sorted(list(d.items())))
        output = 0
        for (i, v), x in d.items():
            if i == len(nums) - 1:
                # print(i, v, x)
                output += x
        #return output
        return output % MOD
