class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), mod = 1e9 +7;
        vector<vector<int>> dp(n, vector<int>(1002, 0));
        vector<vector<int>> pre(n, vector<int>(1002, 0));
        for(int j=0;j<=nums[0];j++){
            dp[0][j] = 1;
            pre[0][j] = j + 1;
        }
        for(int i=1;i<n;i++){
            for(int j=0;j<=nums[i];j++){
                if(min(nums[i-1] - nums[i], 0) + j >= 0)
                    dp[i][j] = pre[i-1][min(nums[i-1] - nums[i], 0) + j];
                pre[i][j] = ((j > 0 ? pre[i][j-1] : 0) + dp[i][j]) % mod; 
            }
        }
        int ans = 0;
        for(int i=0;i<1001;i++){
            ans += dp[n-1][i];
            ans %= mod;
        }
        return ans;
    }
};
